parameters.functionScript:
const videoIds = Inputs.InputArray || [];
if (!videoIds) return;

try {
  Outputs.Videos = videoIds;
  Outputs.mainVideo = videoIds.filter(item => item.type === "main");
  Outputs.introVideo = videoIds.filter(item => item.type === "intro") || '';
  Outputs.outroVideo = videoIds.filter(item => item.type === "outro") || '';
  Outputs.Success();
} catch (error) {
  Outputs.error = error;
  Outputs.Failure();
}

const seconds = Math.round(Inputs.Seconds) || 0;

// Check if the input is a number
if (isNaN(seconds)) throw new Error('Invalid input. Please input a number');

// Calculate hours, minutes and seconds
const hours = Math.floor(seconds / 3600);
const minutes = Math.floor((seconds % 3600) / 60);
const remainingSeconds = seconds % 60;

// Format the time as HH:MM:SS
const formattedTime = [hours, minutes, remainingSeconds]
  .map(unit => unit.toString().padStart(2, '0')) // Pad the time units with leading zeros
  .join(':');

Outputs.FormattedTime = formattedTime;
Outputs.Success();

const data = Inputs.hasVideos; // Assuming 'hasVideos' is the input connected to this node

const videos = data.map(item => {
    if (item.Video_id) {
        // Extract only the properties inside Video_id, excluding 'id'
        const { ...rest } = item.Video_id;
        return rest;
    } else {
        return {}; // Return an empty object if Video_id is missing or invalid
    }
});

Outputs.videos = videos;

const array = Inputs.Array || [];
const start = Inputs.Start || 0;
const end = Inputs.End || Infinity;

try {
  const filteredArray = array.filter(item => item.start >= start && item.start < end);
  Outputs.FilteredArray = filteredArray;
  Outputs.Success();
} catch (error) {
  Outputs.error = error;
  Outputs.Failure();
}

