.masonry-grid {
  display:flex !important; /* Uses flex to allow columns to dynamically size based on the width of their children */
  flex-wrap: wrap; /* Wraps items that overflow the width of the container */
  flex-direction: row !important; /* Override the default column direction */
  gap: 0.05rem; /* Horitontal gap between items */
  align-items: flex-start !important; /* Keeps the row items left aligned */
}

/* Apply this CSS to each item in the Repeater */
.masonry-grid-item {
  flex: 0 0 auto; /* Prevent items from stretching */
  break-inside: avoid; /* Avoids breaking items across rows */
  margin-bottom: 0.3rem; /* Adjust the gap between rows as needed */
  min-width: 0px !important; /* Makes sure the items shrink down to their text width */
}

parameters.functionScript:
const inputArray = Noodl.Array.get('paragraphSelection');

console.log('Input array = ',inputArray);

// const inputArray = Inputs.Array || [];
const id = Inputs.Id || '';

// Filter the array to only include objects where the 'id' field matches the input 'id'
const filteredArray = inputArray.filter(item => item.id === id);

// Output the count of matching objects
Outputs.Count = filteredArray.length;

// If the operation is successful, send the 'Success' signal
Outputs.Success();

// Get the 'paragraphSelection' array from the inputs
const paragraphSelection = Noodl.Arrays.paragraphSelection || [];

// Check if the array is empty
if (paragraphSelection.length === 0) {
    throw new Error('Empty array');
}

// Sort the array in descending order based on the 'order' field
paragraphSelection.sort((a, b) => b.order - a.order);

// Get the 'order' field from the first object in the sorted array and add 1
const highestOrderPlusOne = paragraphSelection[0].order + 1;

// Output the result
Outputs.HighestOrderPlusOne = highestOrderPlusOne;
Outputs.Success();

