const system_retry_cycle_limit = 10; // Number of retry cycles that check if the SDK Client and the refresh method are available for use
const system_retry_cycle_duration = 1000; // Delay between each retry cycle, in milliseconds

const retry_loop_limit = 3; // Number of Request Execution loops, per retry cycle
const retry_loop_duration = 250; // Delay between each loop execution, in milliseconds

////////////////////////////////////////////

const sleep = async function (ms) {
    return new Promise(resolve => {
        setTimeout(resolve, ms);
    });
}

const main = async function (client, count) {
    try {
        const result = await client.refresh();

        Outputs.data = result;
        Outputs.Success();
        return true;

    } catch (err) {
        console.error(err);

        if (count < retry_loop_limit) {
            count++;
            await sleep(count * retry_loop_duration);
            await main();
        } else {
            Outputs.error = err?.errors?.map(i => i.message).join("\n");
            Outputs.Failure();
        }
    }
    return false;
}

