do {
    count++;
    let condition = Boolean(Noodl.Objects["SDK Client set"]) && Boolean(Noodl.Objects.DirectusSDK.Client && Boolean(Noodl.Objects.DirectusSDK.Client.refresh));

    if (condition) {
        const client = Noodl.Objects.DirectusSDK.Client;
        const res = await main(client, count);

        if (res) count = 0;

    } else {
        await sleep(system_retry_cycle_duration);
    }

} while (count > 0 && count < system_retry_cycle_limit);

