function map_id_field(data, id_new) {
    if (id_new && id_new != "" && data) {
        if (Array.isArray(data) && data.length > 0) {

            let temp_res = data.map(i => {
                let temp = {
                    ...i
                };
                if (i.id) temp[id_new] = i.id;
                return temp;
            });
            return temp_res;

        } else if (typeof (data) == "object") {

            let temp = {
                ...data
            };
            if (data.id) temp[id_new] = data.id;
            return temp;

        }
    }
    return data;
}


