const inputDate = Inputs.ISODate || '';
if (!inputDate) throw new Error('Invalid date');

try {
  const date = new Date(inputDate);
  const now = new Date();
  const diffMs = now - date;
  const diffSecs = Math.floor(diffMs / 1000);
  const diffMins = Math.floor(diffSecs / 60);
  const diffHours = Math.floor(diffMins / 60);
  const diffDays = Math.floor(diffHours / 24);

  let formattedDate;
  if (diffDays > 0) {
    if (diffDays > 7) {
      // Format date as DD-MMM-YY
      const day = date.getDate();
      const month = date.toLocaleString('default', { month: 'short' });
      const year = date.getFullYear().toString().substr(-2);
      formattedDate = `${day}-${month}-${year}`;
    } else {
      // Format as 'X days ago'
      formattedDate = `${diffDays} days ago`;
    }
  } else if (diffHours > 0) {
    // Format as 'X hours ago'
    formattedDate = `${diffHours} hours ago`;
  } else if (diffMins > 0) {
    // Format as 'X minutes ago'
    formattedDate = `${diffMins} minutes ago`;
  } else {
    // Format as 'Just now'
    formattedDate = 'Just now';
  }

  Outputs.FormattedDate = formattedDate;
  Outputs.Success();
} catch (error) {
  Outputs.error = error;
  Outputs.Failure();
}

