const videoFile = Inputs.VideoFile;  // Noodl input for a local video file
const videoUrl = Inputs.VideoUrl || '';  // Noodl input for a remote video URL

if (!videoFile && !videoUrl) {
    Outputs.Failure();  // Fail if neither a file nor a URL is provided
    return;
}

try {
    const videoElement = document.createElement('video');
    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');

    if (videoFile) {
        // Handle local file
        videoElement.src = URL.createObjectURL(videoFile);
    } else if (videoUrl) {
        // Handle remote URL
        videoElement.src = videoUrl;
        videoElement.crossOrigin = 'anonymous'; // Allow cross-origin access if needed
    }

    videoElement.addEventListener('loadeddata', () => {
        // Seek to the middle of the video
        videoElement.currentTime = videoElement.duration / 2;
    });

    videoElement.addEventListener('seeked', () => {
        // Set canvas size to video dimensions
        canvas.width = videoElement.videoWidth;
        canvas.height = videoElement.videoHeight;

        // Draw the video frame onto the canvas
        context.drawImage(videoElement, 0, 0, videoElement.videoWidth, videoElement.videoHeight);

        // Convert canvas to an image (base64 format)
        const thumbnailImage = canvas.toDataURL('image/png');

        // Output the thumbnail image
        Outputs.ThumbnailImage = thumbnailImage;
        Outputs.Success();  // Trigger Success signal
    });

    videoElement.addEventListener('error', () => {
        Outputs.Failure();  // Handle video loading errors
    });

} catch (error) {
    console.error("Error generating thumbnail:", error);
    Outputs.Failure();  // Trigger Failure signal
}

