const filePath = `${companyId}/${fileName}`; // e.g., 'company123/d3b07384_document.pdf'

// Construct the URL for the file in Azure Blob Storage
const fileUrl = `https://${storageAccountName}.blob.core.windows.net/${containerName}/${filePath}`;
const uploadUrl = `${fileUrl}?${sasToken}`;

try {
