  xhr.setRequestHeader('x-ms-blob-type', 'BlockBlob');
  xhr.setRequestHeader('Content-Type', file.type);

  xhr.upload.onprogress = function (event) {
    if (event.lengthComputable) {
      const percentComplete = Math.round((event.loaded / event.total) * 100);
      Outputs.progress = percentComplete; // Output the progress percentage
    }
  };

  xhr.onload = function () {
    if (xhr.status >= 200 && xhr.status < 300) {
      // File successfully uploaded, return relevant data
      const fileData = {
        url: fileUrl, // URL to access the file
        name: fileName, // Name of the uploaded file
        storageAccount: storageAccountName, // Storage account name
        container: containerName, // Container name
      };

      Outputs.fileData = fileData; // Store file data in Outputs for further use
      Outputs.Success(); // Indicate success
    } else {
      Outputs.error = xhr.statusText || 'Upload failed';
      Outputs.Failure(); // Indicate failure
    }
  };

  xhr.onerror = function () {
    Outputs.error = xhr.statusText || 'An error occurred';
    Outputs.Failure(); // Indicate failure
  };

  xhr.send(file); // Send the file
} catch (error) {
  Outputs.error = error.message || 'An error occurred'; // Provide a meaningful error message
  Outputs.Failure(); // Indicate failure
}

