    var client = {};

    if (crossDomainCookies) {
        if (staticToken && staticToken != "") {
            // Static Token has been provided //
            client = Directus.createDirectus(project_url).with(Directus.staticToken(staticToken), { credentials: 'include' }).with(Directus.rest({ credentials: 'include' }));
        } else {
            // No Static Token. Connecting via the default Public Access Role //
            client = Directus.createDirectus(project_url).with(Directus.rest({ credentials: 'include' }));
        }
    } else {
        if (staticToken && staticToken != "") {
            // Static Token has been provided //
            client = Directus.createDirectus(project_url).with(Directus.staticToken(staticToken)).with(Directus.rest());
