const file = Inputs.File || null; // File from file picker node
const sasToken = Inputs.SasToken || 'sv=2022-11-02&ss=b&srt=sco&sp=rwdlaciytfx&se=2025-08-01T18:06:34Z&st=2024-08-05T10:06:34Z&spr=https,http&sig=U2aHk1OiRypOFtdXZBr9kYa%2F8X6xt0rnVwk5h%2FSruDA%3D'; // Shared Access Signature (SAS) token
const storageAccountName = Inputs.StorageAccountName || 'sacdnvhivedev002'; // Azure storage account name
const containerName = Inputs.ContainerName || 'hive-clip-noodl-storage'; // Azure storage container name
const companyId = Inputs.CompanyId || 'defaultCompanyId'; // Unique object ID for the company

if (!file) throw new Error('No file provided');
if (!sasToken) throw new Error('Invalid SAS token');
if (!storageAccountName) throw new Error('Invalid storage account name');
if (!containerName) throw new Error('Invalid container name');
if (!companyId) throw new Error('Invalid company ID');

// Generate a unique identifier, e.g., UUID
const uniqueId = generateUUID(); // Implement or import a function to generate a UUID

// Construct a unique file name with company ID as the folder and unique ID as a prefix
const fileName = `${uniqueId}_${file.name}`; // e.g., 'd3b07384_document.pdf'
const filePath = `${companyId}/${fileName}`; // e.g., 'company123/d3b07384_document.pdf'

// Construct the URL for the file in Azure Blob Storage
const fileUrl = `https://${storageAccountName}.blob.core.windows.net/${containerName}/${filePath}`;
const uploadUrl = `${fileUrl}?${sasToken}`;

try {
  const xhr = new XMLHttpRequest();
  xhr.open('PUT', uploadUrl, true);
  xhr.setRequestHeader('x-ms-blob-type', 'BlockBlob');
  xhr.setRequestHeader('Content-Type', file.type);

  xhr.upload.onprogress = function (event) {
    if (event.lengthComputable) {
      const percentComplete = Math.round((event.loaded / event.total) * 100);
      Outputs.progress = percentComplete; // Output the progress percentage
    }
  };

  xhr.onload = function () {
    if (xhr.status >= 200 && xhr.status < 300) {
      // File successfully uploaded, return relevant data
      const fileData = {
        videoUrl: fileUrl, // URL to access the file
        videoName: fileName, // Name of the uploaded file
        storageAccount: storageAccountName, // Storage account name
        container: containerName, // Container name
      };

      Outputs.fileData = fileData; // Store file data in Outputs for further use
      Outputs.Success(); // Indicate success
    } else {
      Outputs.error = xhr.statusText || 'Upload failed';
      Outputs.Failure(); // Indicate failure
    }
  };

  xhr.onerror = function () {
    Outputs.error = xhr.statusText || 'An error occurred';
    Outputs.Failure(); // Indicate failure
  };

  xhr.send(file); // Send the file
} catch (error) {
  Outputs.error = error.message || 'An error occurred'; // Provide a meaningful error message
  Outputs.Failure(); // Indicate failure
}

// Function to generate a UUID (placeholder implementation)
function generateUUID() {
  // Generate a random UUID (v4)
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
    const r = (Math.random() * 16) | 0,
      v = c === 'x' ? r : (r & 0x3) | 0x8;
    return v.toString(16);
  });
}

